(function($){wp.media.shibaGalleryMediaManager={init:function(){this.frame=wp.media.frames.shibaGalleryMediaManager=wp.media({title:'Choose Default Image',library:{type:'image'},button:{text:'Set as default image',}});this.frame.on('select',function(){var a=wp.media.shibaGalleryMediaManager.frame.state().get('selection').first();$("input[type=hidden][name='shiba_gallery_options[default_image]']").val(a.attributes.id);$('#shiba-gallery-default-image img').attr("src",a.attributes.url).show();var w=a.attributes.width,h=a.attributes.height});$('#choose-default-image').click(function(a){wp.media.shibaGalleryMediaManager.$el=$(this);a.preventDefault();wp.media.shibaGalleryMediaManager.frame.open()});$('#unset-default-image').click(function(a){$("input[type=hidden][name='shiba_gallery_options[default_image]']").val(0);$('#shiba-gallery-default-image img').hide();a.preventDefault()})}};wp.media.shibaGalleryMediaManager.init()}(jQuery));